import React, {useState, useEffect} from "react";
import Container from "react-bootstrap/container";
import {FontAwesomeIcon} from "@fortawesome/react-fontawesome";
import {faCartShopping, faHeart} from "@fortawesome/free-solid-svg-icons";
import Product from "./Product";
import {Row} from "react-bootstrap";
function isEmpty(val)
{
    return val === "" || val === undefined || val === null || val === [];
}
function ProductApp() {
    const [products, setProducts] = useState(null);
    const [sortedProducts, setSortedProducts] = useState("");
    //
    const [checked, setChecked] = useState([]);
    //
    useEffect(() => {
        fetchProducts();
    }, [sortedProducts])

    const fetchProducts = async () => {
        let response = await fetch("https://fakestoreapi.com/products");
        let products = await response.json();


        if(!isEmpty(sortedProducts)  && sortedProducts === "+" )
        {
            products = (products.sort((a, b) => a.price > b.price ? 1 : -1));
        }
        else if(!isEmpty(sortedProducts)  && sortedProducts === "-" )
        {
            products = (products.sort((a, b) => a.price < b.price ? 1 : -1));
        }

        setProducts(products);
    }
    const SetPriceSort = (event) =>
    {
        setSortedProducts(event.target.value);
    }

    return (
        <>
            <div >
                <h2>Сортування</h2>
                <div className="input-group">
                    <div className="input-group-prepend">
                        <span className="input-group-text" id="">За альбомом у порядку зростання: </span>
                        <input type="text" defaultValue={null} className="form-control"
                               onInput={SetPriceSort}/>
                    </div>
                    <div>
                        <span className="input-group-text" id="">Від найменшої ціни:</span>
                        <input value={item} type="checkbox" />
                    </div>
                </div>
            </div>
            <div className="card">
                {
                    products && products.map((product) => {
                        return (
                            <Product product={product}/>
                        )
                    })
                }
            </div>
        </>


    );

}

export default ProductApp;
